import React from 'react';
import mainStyles from './main.module.css';
import { useState } from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';
import PropType from  'prop-types';


class Main extends React.Component {
  render() {
    const [current, setCurrent] = State('buns');
    const [modalActive, setModalActive] = State(false);

    const [activeIngredient, setModalActiveIngredient] = State();

    const openIngredientDetails = (ingredient) => {
      setModalActive (true);
      setModalActiveIngredient (ingredient);
    };


    return (
      <main className={mainStyles.main}>
   
        <section className={mainStyles.section}>
          <h1 className={mainStyles.title}>Соберите бургер</h1>
          <div>
     <Tab />
     <Tab />
    </div>
        </section>

      </main>
    );
  }
}

export default Main;
