import { useState } from "react";
import BurgerIngredients from "../burger-Ingredients/burger-Ingredients";
import burgerIngredientStyle from './burger-Ingredients.module.css';
import {Tab} from '@ya.praktikum/react-developer-burger-ui-components';
import PropTypes from "prop-types";


const BurgerIngredient = ({data}) => {
const [current, setCurrent] = useState('bun')
const [modallActive, setModalActive] = userState(false);
const [activeIngredient, setActiveIngredient] = userState();

const openIngridientDetails = (ingredient) => {
    setModalActive(true);
    setActiveIngredient(ingredient);
};

return (

<section className={burgerIngredientsStyle.section}>
          <h1 className={burgerIngredienstStyle.title}>Соберите бургер</h1>
          
           
          
        <Tab value="bun" active={current === 'bun'} onClick={setCurrent}>
          Булки
        </Tab>
    
    
        <Tab value="sauces" active={current === 'sauces'} onClick={setCurrent}>
          Соусы
        </Tab>
     
      
        <Tab value="fillings" active={current === 'fillings'} onClick={setCurrent}>
          Начинки
        </Tab>
     
      
        </section>

)



}

export default BurgerIngredients;
