import React from 'react';
import mainStyles from './main.module.css';
import { useState } from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';



class Main extends React.Component {
  render() {
    const [current, setCurrent] = useState('buns');
    const [modalActive, setModalActive] = useState(false);

    const [activeIngredient, setModalActiveIngredient] = userState();

    const openIngredientDetails = (ingredient) => {
      setModalActive (true);
      setModalActiveIngredient (ingredient);
    };


    return (
      <main className={mainStyles.main}>
   
        <section className={mainStyles.section}>
          <h1 className={mainStyles.title}>Соберите бургер</h1>
          <div>
      <a href="#buns" id='bunsAnc'>
        <Tab value="buns" active={current === 'buns'} onClick={setCurrent}>
          Булки
        </Tab>
      </a>
      <a href="#sauces" id='sauceAnc'>
        <Tab value="sauces" active={current === 'sauces'} onClick={setCurrent}>
          Соусы
        </Tab>
      </a>
      <a href="#fillings" id='fillingsAnc'>
        <Tab value="fillings" active={current === 'fillings'} onClick={setCurrent}>
          Начинки
        </Tab>
      </a>
    </div>
        </section>

      </main>
    );
  }
}

export default Main;
